import React, { Component } from "react";
import Square from "../square/Square";
import classes from "./Board.module.css";
import Game from "../../logic/Game";

class Board extends Component {
  state = {
    game: new Game(),
  };

  resetBoard = () => {
    this.setState({
      game: new Game(),
    });
  };

  renderSquare(i) {
    return (
      <Square
        value={this.state.game.squares[i]}
        onClick={() => this.clickHandler(i)}
      />
    );
  }

  clickHandler = (i) => {
    const game = this.state.gane;
    if (game.calculateWinner()) {
      console.log(`Disabling clickHandler as winner is ${winner}`);
      return;
    }
    game.setAction("X", i);
    const action = game.getAIAction();
    if (action) {
      game.setAction("X", i);
    }
    const squares = this.state.squares.slice();
    squares[i] = "X";

    winner = this.calculateWinner(squares);
    if (winner !== "X") {
      console.log(`calling goetAIAction for ${squares}`);
      let action = this.getAIAction(squares);
      squares[action] = "O";
      console.log(` ... dine with goetAIAction board now is ${squares}`);
    } else {
      console.log(`X must be the ${winner}`);
    }
    winner = this.calculateWinner(squares);
    if (winner !== "Z") {
      winner = winner === "X" ? "You" : "AI";
    }

    this.setState({
      squares: squares,
      winner: winner,
    });
  };

  getAIAction = (squares) => {
    // return center or top-left in first AI move
    if (squares.filter((s) => s === "X").length === 1) {
      if (squares[4] === ":") {
        return 4;
      } else if (squares[0] === ":") {
        return 0;
      }
    }
    // use minmax algorithm
    // find action with least value by choosing opp's action with highest value
    let [next_val, action] = this.minmax(squares, "O");
    console.log(`getAIAction Got ${next_val} ${action} from minmax`);
    if (action == null) {
      action = squares.findIndex((s) => s === ":");
    }
    return action;
  };

  render() {
    const winner = this.state.game.calculateWinner();
    let status;
    if (winner) {
      status = `Winner: ${winner}`;
    } else {
      status = `Next player: You`;
    }

    const resetStyle = {
      backgroundColor: "green",
      fontSize: "10px",
      borderRadius: "4px",
    };
    return (
      <div>
        <div>
          <p className={classes.Status}>{status}</p>
        </div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
        <div className="board-row">
          <button style={resetStyle} onClick={this.resetBoard}>
            Reset
          </button>
        </div>
      </div>
    );
  }
}

export default Board;
